                    vertical: top.substr(0, 3) == '110',

                    
                    top:    max(left.substr(3, 2), left.substr(5, 2)),
                    right:  max(left.substr(5, 2), top.substr(3, 2)),
                    bottom: max(top.substr(3, 2), top.substr(5, 2)),
